        return 6;
    }

    /**
     * Private Booster distribution function
     */
     function burnAndBoost(uint czxpWager) private returns(uint16[7]) {
         //default distribution is 1,50,2700,10000
         uint16[7] memory probs = [0,0,0,1,20,2700,10000];

         //if wager is greater than 0, get new distribution
         if(czxpWager > 0){
                   // 5 * (10**7)
         //fixed x = czxpWager/300000;
         //ufixed dist = 2.7182818**x;
         }

        return probs;
     }


//Thanks! for supporting the Cryptoz universe <3 <3 <3
    /**
    * Withdraw balance to wallet
    **/
   function withdraw() public onlyOwner {
      msg.sender.transfer(address(this).balance);
   }
}
