     */
    function awardCzxp(address _beneficiary, uint czxpReward) internal {
        //Give the czxp tokens
        CzxpToken(CzxpContractAddress_).awardCzxp(_beneficiary, czxpReward);
    }

    /**
     * Random as it gets for now. Return a val between 1-100
     */
    function selectRandom(uint seed, uint256 range) view internal returns(uint){
        return(uint(keccak256(block.blockhash(block.number-1), seed ))%range);
    }

